import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Alert,
  TextInput,
  KeyboardAvoidingView,
  Platform,
  TouchableWithoutFeedback,
  Keyboard,
  Image,
  ImageBackground,
} from "react-native";

// const image = { uri: "https://reactjs.org/logo-og.png" };

export default function App() {
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");

  const nameHandler = (text) => setName(text);
  const passwordHandler = (text) => setPassword(text);
  const emailHandler = (text) => setEmail(text);

  const onLogin = () => {
    Alert.alert("Credentials", `${name} + ${password}+${email}`);
  };

  return (
    <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
      <View style={styles.container}>
        <ImageBackground
          source={require("./assets/images/photoBg.jpg")}
          style={styles.image}
        ></ImageBackground>
        <Text style={styles.text}>Регистрация</Text>
        <KeyboardAvoidingView
          behavior={Platform.OS == "ios" ? "padding" : "height"}
        >
          <View style={styles.form}>
            <View>
              <TextInput
                value={name}
                placeholder="Логин"
                onChangeText={nameHandler}
                style={styles.input}
              />
            </View>
            <View>
              <TextInput
                value={password}
                placeholder={"Адрес электронной почты"}
                onChangeText={passwordHandler}
                style={styles.input}
              />
            </View>
            <View>
              <TextInput
                value={email}
                placeholder="Пароль"
                onChangeText={emailHandler}
                style={styles.input}
              />
            </View>
            <TouchableOpacity
              onPress={onLogin}
              activeOpacity={0.8}
              style={styles.btn}
            >
              <Text style={styles.btnTitle}>Зарегистрироваться</Text>
            </TouchableOpacity>
            <View display={"flex"} justifyContent={"center"}>
              <Text style={styles.textBottom}>Уже есть аккаунт?</Text>
              <TouchableOpacity style={styles.btnLogIn} onPress={onLogin}>
                <Text style={styles.textBottom}>Войти</Text>
              </TouchableOpacity>
            </View>
            {/* <Button onPress={onLogin} title={"Зарегистрироваться"} style={styles.button} /> */}
          </View>
        </KeyboardAvoidingView>
        <StatusBar style="auto" />
      </View>
    </TouchableWithoutFeedback>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#ffffff",
    // background: url(photo-1536528087222-ef43dd3bb0f3.jpg),
    // alignItems: "center",
    justifyContent: "center",
    // justifyContent: "flex-end",
    // paddingBottom: 100,
    // paddingTop: 30
  },
  image: {
    flex: 1,
    // width: 375,
    // height: 812,
    resizeMode: "cover",
    justifyContent: "center",
    alignItems: "center",
  },

  text: {
    color: "#212121",
    fontSize: 30,
    textAlign: "center",
    marginBottom: 33,
    marginTop: 92,
  },
  input: {
    width: 343,
    height: 50,
    backgroundColor: "#F6F6F6",
    paddingRight: 16,
    paddingLeft: 16,
    borderWidth: 1,
    borderColor: "#E8E8E8",
    marginBottom: 16,
    marginLeft: 16,
    borderRadius: 8,
  },

  btn: {
    backgroundColor: "#FF6C00",
    height: 51,
    borderRadius: 100,
    marginTop: 43,
    justifyContent: "center",
    alignItems: "center",
    marginHorizontal: 16,
    marginBottom: 16,
  },
  btnTitle: {
    color: "#f0f8ff",
    fontSize: 18,
  },
  textBottom: {
    color: "#212121",
    fontSize: 16,
    textAlign: "center",
    // marginBottom: 33,
    marginTop: 92,
  },
  btnLogIn: {
    color: "#212121",
    fontSize: 16,
    textAlign: "center",
    marginBottom: 33,
    marginTop: 92,
  },
});
